package common;

import java.io.IOException;
import java.io.InputStream;
import java.util.Properties;

public class GlobalVariables {
	
	private final static String sampleCodeVersion = "4.0.0.1";
	
    private static Properties properties = new Properties();

    // For test cards, use TEST values for 'Root Certificate' and 'CA Certificate Common Name' variables.
    // For real cards, use REAL values for 'Root Certificate' and 'CA Certificate Common Name' variables.

    // REAL: ""
    // TEST: "
    public static String mdasRootCertificate = "";

    // REAL: ""
    // TEST: "
    public static String cardRootCertificate = "";

    // REAL: "IRAN Digital ID CA2"
    // TEST: "TestBED2 IRAN Digital ID CA2"
    public static String mdasCACertCN = "TestBED2 IRAN Digital ID CA2";

    // REAL: "IRAN Digital Citizen CA 20"
    // TEST: "Matiran Citizen CA 20"
    public static String cardCACertCN = "Matiran Citizen CA 20";

    public static String mdasPolicyIdentifier = "2.16.364.101.26.1.1.3.101.4";
    public static String cardPolicyIdentifier = "2.16.364.101.26.1.1.2.101.2";

    public static String signatureAlgorithm = "1.2.840.113549.1.1.11";
    public static String hashAlgorithm = "2.16.840.1.101.3.4.2.1";
    public static String algorithmVersion = "1.00";

    public static long validTimestampDifference = 60 * 1000;

    public static String exceptionSourceSample = "Sample";
    public static String exceptionSourceMDAS = "MDAS";


    private static String SPID;
    private static String serverAddress;
    private static String keyStoreName;
    private static String keyStorePassword;
    private static String version = "v1.0";

    public static String getSPID()  {
        if (SPID == null || SPID.isEmpty())
            SPID = getProperty("SPID");
        return SPID;
    }

    public static String getVersion()  {
        if (version == null || version.isEmpty())
            version = getProperty("version");
        return version;
    }


    public static String getServerAddress() {
        if (serverAddress == null || serverAddress.isEmpty())
            serverAddress = getProperty("serverAddress");
        return serverAddress;
    }

    public static String getKeyStoreName() {
        if (keyStoreName == null || keyStoreName.isEmpty())
            keyStoreName = getProperty("keyStoreName");
        return keyStoreName;
    }

    public static String getKeyStorePassword() {
        if (keyStorePassword == null || keyStorePassword.isEmpty())
            keyStorePassword = getProperty("keyStorePassword");
        return keyStorePassword;
    }

    private static String getProperty(String propertyName) {
        String propertyValue = null;
        InputStream input = null;
        try {
            input = GlobalVariables.class.getClassLoader().getResourceAsStream("config.properties");
            properties.load(input);
            if (!properties.getProperty(propertyName).isEmpty())
                propertyValue = properties.getProperty(propertyName);
            else
                propertyValue = GlobalVariables.class.getField(propertyName).get(null).toString();
        } catch (IOException ex) {
            ex.printStackTrace();
        } catch (IllegalAccessException e) {
            e.printStackTrace();
        } catch (NoSuchFieldException e) {
            e.printStackTrace();
        } finally {
            if (input != null)
                try {
                    input.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
        }
        return propertyValue;
    }
}
